      out.write("\r\n");
      out.write("	function moveLeft() {\r\n");
      out.write("		elementLeft.onclick = function() {\r\n");
      out.write("			if (active === 0) {\r\n");
      out.write("				active = length;\r\n");
      out.write("			}\r\n");
      out.write("			active--;\r\n");
      out.write("			reloadSlider();\r\n");
      out.write("		};\r\n");
      out.write("	}\r\n");
      out.write("\r\n");
      out.write("	function moveRight() {\r\n");
      out.write("		elementRight.onclick = function() {\r\n");
      out.write("			if (active === length - 1) {\r\n");
      out.write("				active = -1;\r\n");
      out.write("			}\r\n");
      out.write("			active++;\r\n");
      out.write("			reloadSlider();\r\n");
      out.write("		};\r\n");
      out.write("	}\r\n");
      out.write("\r\n");
      out.write("	function autoSlider() {\r\n");
