      out.write("	}\r\n");
      out.write("\r\n");
      out.write("	function reloadSlider() {\r\n");
      out.write("		var checkLeft = elementImgItem[active].offsetLeft;\r\n");
      out.write("		elementImgList.style.left = -checkLeft + \"px\";\r\n");
      out.write("\r\n");
